HttpResponse fireRequest(String URL,Method method,HttpResponse fireRequest,String URL,Method method,Map<String, String> params,HttpResponse fireRequest,String URL,Method method,Map<String, String> params,Object data,boolean guaranteeSSL,HttpResponse fireRequest,String URL,Method method,Map<String, String> params,Map<String, String> headers,Object data,boolean guaranteeSSL,HttpResponse fireRequest,HttpClientWrapper httpClientWrapper,String URL,Method method,Map<String, String> params,Object data,boolean guaranteeSSL,HttpResponse fireRequest,HttpClientWrapper httpClientWrapper,String URL,Method method,Map<String, String> params,Map<String, String> headers,Object data,boolean guaranteeSSL){
