String dereferenceValue(String value){
    if (log.isDebugEnabled())
        log.debug("dereferenceValue(" + value + ")");
    String drValue = value;
    if (value != null && value.length() >= 4) {
        Matcher matcher = referencePattern.matcher(value);
        if (matcher.find()) {
            if (log.isDebugEnabled())
                log.debug("dereferenceValue(" + value + "), found refs to replace");
            matcher.reset();
            StringBuilder sb = new StringBuilder();
            int pointer = 0;
            while (matcher.find()) {
                String name = matcher.group(1);
                if (StringUtils.isNotBlank(name)) {
                    String replacementValue = null;
                    ConfigItemImpl ci = findConfigItem(name, null);
                    if (ci != null) {
                        replacementValue = "";
                        if (ci.getValue() != null) {
                            replacementValue = StringUtils.trimToEmpty(ci.getValue().toString());
                        }
                    }
                    sb.append(value.substring(pointer, matcher.start()));
                    if (replacementValue == null) {
                        replacementValue = matcher.group();
                    } else {
                        replacementValue = dereferenceValue(replacementValue);
                    }
                    sb.append(replacementValue);
                    pointer = matcher.end();
                }
            }
            sb.append(value.substring(pointer, value.length()));
            drValue = sb.toString();
        }
    }
    if (log.isDebugEnabled())
        log.debug("dereferenceValue(" + value + "): return=" + drValue);
    return drValue;
}

