void setDefaultTransactionIsolationString(String defaultTransactionIsolation){
    if ((defaultTransactionIsolation == null) || (defaultTransactionIsolation.trim().length() == 0)) {
        setDefaultTransactionIsolation(null);
    } else if (defaultTransactionIsolation.trim().equalsIgnoreCase("TRANSACTION_NONE")) {
        setDefaultTransactionIsolation("TRANSACTION_NONE");
    } else if (defaultTransactionIsolation.trim().equalsIgnoreCase("TRANSACTION_READ_UNCOMMITTED")) {
        setDefaultTransactionIsolation("TRANSACTION_READ_UNCOMMITTED");
    } else if (defaultTransactionIsolation.trim().equalsIgnoreCase("TRANSACTION_READ_COMMITTED")) {
        setDefaultTransactionIsolation("TRANSACTION_READ_COMMITTED");
    } else if (defaultTransactionIsolation.trim().equalsIgnoreCase("TRANSACTION_REPEATABLE_READ")) {
        setDefaultTransactionIsolation("TRANSACTION_REPEATABLE_READ");
    } else if (defaultTransactionIsolation.trim().equalsIgnoreCase("TRANSACTION_SERIALIZABLE")) {
        setDefaultTransactionIsolation("TRANSACTION_SERIALIZABLE");
    } else {
        setDefaultTransactionIsolation(null);
        log.warn("invalid transaction isolation level: " + defaultTransactionIsolation);
    }
}

List<String> dbRead(String sql,List dbRead,String sql,Object[] fields,SqlReader reader,List dbRead,Connection callerConn,String sql,Object[] fields,SqlReader reader){
    if (callerConn == null) {
        callerConn = (Connection) threadLocalManager().get(TRANSACTION_CONNECTION);
    }
    if (log.isDebugEnabled()) {
        log.debug("dbRead(Connection " + callerConn + ", String " + sql + ", Object[] " + Arrays.toString(fields) + ", SqlReader " + reader + ")");
    }
    long start = 0;
    long connectionTime = 0;
    int lenRead = 0;
    long stmtTime = 0;
    long resultsTime = 0;
    int count = 0;
    if (log.isDebugEnabled()) {
        String userId = usageSessionService().getSessionId();
        StringBuilder buf = new StringBuilder();
        if (fields != null) {
            buf.append(fields[0]);
            for (int i = 1; i < fields.length; i++) {
                buf.append(", ");
                buf.append(fields[i]);
            }
        }
        log.debug("Sql.dbRead: " + userId + "\n" + sql + "\n" + buf);
    }
    Connection conn = null;
    PreparedStatement pstmt = null;
    ResultSet result = null;
    ResultSetMetaData meta = null;
    List rv = new Vector();
    try {
        if (m_showSql) {
            start = System.currentTimeMillis();
        }
        if (callerConn != null) {
            conn = callerConn;
        } else {
            conn = borrowConnection();
        }
        if (m_showSql) {
            connectionTime = System.currentTimeMillis() - start;
        }
        if (m_showSql) {
            start = System.currentTimeMillis();
        }
        pstmt = conn.prepareStatement(sql);
        prepareStatement(pstmt, fields);
        result = pstmt.executeQuery();
        if (m_showSql) {
            stmtTime = System.currentTimeMillis() - start;
        }
        if (m_showSql) {
            start = System.currentTimeMillis();
        }
        while (result.next()) {
            if (m_showSql) {
                count++;
            }
            if (reader == null) {
                String s;
                ResultSetMetaData metadataResult = result.getMetaData();
                if (metadataResult != null && Types.CLOB == metadataResult.getColumnType(1)) {
                    Clob clobResult = result.getClob(1);
                    s = clobResult.getSubString(1, (int) clobResult.length());
                } else {
                    s = result.getString(1);
                }
                if (s != null) {
                    rv.add(s);
                }
            } else {
                try {
                    Object obj = reader.readSqlResultRecord(result);
                    if (obj != null) {
                        rv.add(obj);
                    }
                } catch (SqlReaderFinishedException e) {
                    break;
                }
            }
        }
    } catch (SQLException e) {
        log.warn("Sql.dbRead: sql: " + sql + debugFields(fields), e);
    } catch (UnsupportedEncodingException e) {
        log.warn("Sql.dbRead: sql: " + sql + debugFields(fields), e);
    } finally {
        if (m_showSql) {
            resultsTime = System.currentTimeMillis() - start;
        }
        if (null != result) {
            try {
                result.close();
            } catch (SQLException e) {
                log.warn("Sql.dbRead: sql: " + sql + debugFields(fields), e);
            }
        }
        if (null != pstmt) {
            try {
                pstmt.close();
            } catch (SQLException e) {
                log.warn("Sql.dbRead: sql: " + sql + debugFields(fields), e);
            }
        }
        if (callerConn == null) {
            if (null != conn) {
                if (m_commitAfterRead) {
                    try {
                        conn.commit();
                    } catch (SQLException e) {
                        log.warn("Sql.dbRead: sql: " + sql + debugFields(fields), e);
                    }
                }
                returnConnection(conn);
            }
        }
    }
    if (m_showSql)
        debug("Sql.dbRead: time: " + connectionTime + " / " + stmtTime + " / " + resultsTime + " #: " + count, sql, fields);
    return rv;
}

