URLRedirect[] validateControllableTemplates(RedirectControllable configControllable){
    List<URLRedirect> redirects = new ArrayList<URLRedirect>();
    String[] templates = configControllable.defineHandledTemplatePatterns();
    if (templates == null || templates.length == 0) {
        throw new IllegalArgumentException("RedirectControllable: invalid defineHandledTemplatePatterns: " + "this should return a non-empty array of templates or the capability should not be used");
    } else {
        for (String template : templates) {
            URLRedirect redirect = null;
            try {
                redirect = new URLRedirect(template);
            } catch (RuntimeException e) {
                throw new IllegalArgumentException("Failed to validate redirect templates from handled template patterns for prefix (" + configControllable.getEntityPrefix() + "): " + e.getMessage(), e);
            }
            redirects.add(redirect);
        }
    }
    return redirects.toArray(new URLRedirect[redirects.size()]);
}

ContentResource mergeResource(Element element,ContentResource mergeResource,Element element,InputStream in){
    BaseResourceEdit resourceFromXml = new BaseResourceEdit(element);
    String id = resourceFromXml.getId();
    BaseResourceEdit edit = null;
    try {
        edit = (BaseResourceEdit) addResource(id);
    } catch (IdUsedException e) {
        return null;
    }
    edit.setContentType(resourceFromXml.getContentType());
    ResourcePropertiesEdit p = edit.getPropertiesEdit();
    p.clear();
    p.addAll(resourceFromXml.getProperties());
    if (in != null) {
        edit.setContent(in);
    }
    edit.setEvent(EVENT_RESOURCE_ADD);
    assureResourceProperties(edit);
    if (overQuota(edit)) {
        throw new OverQuotaException(edit.getReference());
    }
    m_storage.commitResource(edit);
    if (!readyToUseFilesizeColumn()) {
        addSizeCache(edit);
    }
    String ref = edit.getReference(null);
    eventTrackingService.post(eventTrackingService.newEvent(((BaseResourceEdit) edit).getEvent(), ref, true, NotificationService.NOTI_NONE));
    postAvailableEvent(edit, ref, NotificationService.NOTI_NONE);
    ((BaseResourceEdit) edit).closeEdit();
    return edit;
}

