EntityView makeEntityView(EntityReference ref,String viewKey,String extension){
    if (ref == null) {
        throw new IllegalArgumentException("ref cannot be null");
    }
    EntityView view = new EntityView();
    EntityViewUrlCustomizable custom = (EntityViewUrlCustomizable) entityProviderManager.getProviderByPrefixAndCapability(ref.getPrefix(), EntityViewUrlCustomizable.class);
    if (custom != null) {
        view.loadParseTemplates(custom.getParseTemplates());
    }
    view.setEntityReference(ref);
    if (viewKey != null) {
        view.setViewKey(viewKey);
    }
    if (extension != null) {
        view.setExtension(extension);
    }
    return view;
}

