String checkContent(String format,String content,String refKey,HashMap<String, String> dataMap){
    String dataKey = null;
    if (content != null) {
        content = content.trim();
        if (!"".equals(content)) {
            if (entityEncodingManager.validateFormat(content, format)) {
                if (Formats.XML.equals(format) || Formats.HTML.equals(format)) {
                    content = stripOutXMLTag(content, "<?", "?>");
                    content = stripOutXMLTag(content, "<!DOCTYPE", ">");
                }
                dataKey = UNIQUE_DATA_PREFIX + refKey;
                dataMap.put(dataKey, content);
            }
        }
    }
    return dataKey;
}

